                  <div className="flex items-center justify-end gap-2">
                    {record.status === 'answering' ? (
                      <button onClick={() => setActiveInterview(record)} className="flex items-center gap-1 px-3 py-1.5 bg-emerald-400 text-white rounded-lg text-xs font-bold hover:bg-emerald-500">
                        <Video size={14} /> 进入面试
                      </button>
                    ) : record.status === 'submitted' ? (
                      <button onClick={() => setViewingReport(record)} className="flex items-center gap-1 px-3 py-1.5 bg-violet-400 text-white rounded-lg text-xs font-bold hover:bg-violet-500">
                        <FileText size={14} /> 查看报告
                      </button>
                    ) : (
                      <button 
                        onClick={() => handleEditRecord(record)}
                        className="px-3 py-1.5 bg-gray-100 text-gray-500 rounded-lg text-xs font-bold hover:bg-gray-200"
                      >
                        修改安排
                      </button>
                    )}
                    <button className="p-1.5 text-gray-400 hover:bg-gray-50 rounded-lg transition-all">
                      <MoreHorizontal size={18} />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <CandidateDetailModal isOpen={isDetailModalOpen} candidate={selectedCandidate} onClose={() => setIsDetailModalOpen(false)} />

      <ScheduleInterviewModal 
        isOpen={isScheduleModalOpen}
        onClose={() => setIsScheduleModalOpen(false)}
        onSave={handleSaveInterview}
        record={editingRecord}
        candidates={initialCandidates}
        templates={initialTemplates}
      />
    </div>
  );
};

export default InterviewRecords;
