import React from 'react';
import { ListTodo, Users, User } from 'lucide-react';

interface MobileBottomNavProps {
  activePage: string;
  setActivePage: (page: string) => void;
}

const MobileBottomNav: React.FC<MobileBottomNavProps> = ({ activePage, setActivePage }) => {
  const navItems = [
    { 
      id: 'todo', 
      label: '工作台', 
      icon: ListTodo,
      targetPage: 'todo_interviews' // 映射到待办-面谈，或者 todo_plans
    },
    { 
      id: 'team', 
      label: '团队', 
      icon: Users,
      targetPage: 'my_team_sub'
    },
    { 
      id: 'me', 
      label: '我的', 
      icon: User,
      targetPage: 'dashboard'
    }
  ];

  return (
    <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 h-16 flex items-center justify-around z-50 md:hidden pb-safe">
      {navItems.map((item) => {
        // 判断是否激活 (简单判断 targetPage 是否匹配，或者是父级 id)
        const isActive = activePage === item.targetPage || activePage.startsWith(item.id);

        return (
          <button
            key={item.id}
            onClick={() => setActivePage(item.targetPage)}
            className={`flex flex-col items-center justify-center w-full h-full space-y-1 ${
              isActive ? 'text-primary-600' : 'text-gray-400 hover:text-gray-600'
            }`}
          >
            <item.icon size={24} strokeWidth={isActive ? 2.5 : 2} />
            <span className="text-[10px] font-medium">{item.label}</span>
          </button>
        );
      })}
    </div>
  );
};

export default MobileBottomNav;

import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import TopNav from './components/TopNav';
import MobileBottomNav from './components/MobileBottomNav';
import InterviewList from './components/InterviewList';
import InterviewForm from './components/InterviewForm';
import InterviewExecution from './components/InterviewExecution';
import InterviewScheduler from './components/InterviewScheduler';
import ScheduleMeetingModal from './components/ScheduleMeetingModal'; 
import TemplateConfigPage from './components/TemplateModal'; 
import AssessmentList from './components/AssessmentList';
import AssessmentTaskList from './components/AssessmentTaskList';
import NewInterviewModal from './components/NewInterviewModal';
import TaskList from './components/TaskList'; 
import EmployeeTaskTable from './components/EmployeeTaskTable'; 
import EmployeeDashboard from './components/EmployeeDashboard';
import SystemSettings from './components/SystemSettings';
import PerformanceArchives from './components/PerformanceArchives';
import MyTeamOrgPerformance from './components/MyTeamOrgPerformance';
import MyTeamSubordinatePerformance from './components/MyTeamSubordinatePerformance';
import InterviewConfirmationView from './components/InterviewConfirmationView'; // New
import { InterviewSession, Status, InterviewType, Notification } from './types';
import { MOCK_SESSIONS, MOCK_EMPLOYEES } from './constants';

function App() {
  const [userRole, setUserRole] = useState<'HR' | 'Employee'>('Employee'); 
  const [activePage, setActivePage] = useState('dashboard'); 

  // Assessment Navigation State
  const [selectedAssessmentTask, setSelectedAssessmentTask] = useState<string | null>(null);

  const [sessions, setSessions] = useState<InterviewSession[]>(MOCK_SESSIONS);
  const [selectedSession, setSelectedSession] = useState<InterviewSession | null>(null);

  // Notification State
  const [notifications, setNotifications] = useState<Notification[]>([
      { id: 'n1', targetRole: 'Employee', type: 'system', title: '系统通知', content: '欢迎使用智慧绩效面谈模块', time: '1小时前', read: false },
      { id: 'n2', targetRole: 'HR', type: 'task', title: '待办提醒', content: '您有 3 个面谈任务即将截止', time: '2小时前', read: false }
  ]);

  // View Modes: list, detail, execution, schedule
  const [viewMode, setViewMode] = useState<'list' | 'detail' | 'execution' | 'schedule'>('list');

  const [isScheduleModalOpen, setScheduleModalOpen] = useState(false);
  const [scheduleTargetSession, setScheduleTargetSession] = useState<InterviewSession | null>(null);

  // New Interview Modal State
  const [isNewInterviewModalOpen, setIsNewInterviewModalOpen] = useState(false);
  const [newInterviewDefaultTopic, setNewInterviewDefaultTopic] = useState('绩效面谈');
  const [newInterviewInitialEmployees, setNewInterviewInitialEmployees] = useState<string[]>([]);

  const handleRoleChange = (role: 'HR' | 'Employee') => {
      setUserRole(role);
      if (role === 'HR') {
          setActivePage('assessments');
          setSelectedAssessmentTask(null);
      } else {
          setActivePage('dashboard');
      }
      setViewMode('list');
      setSelectedSession(null);
  };

  const handleMarkNotificationRead = (id: string) => {
      setNotifications(prev => prev.map(n => n.id === id ? { ...n, read: true } : n));
  };

  const handleNotificationClick = (notification: Notification) => {
      handleMarkNotificationRead(notification.id);
      if (notification.targetRole === 'Employee') {
          setUserRole('Employee');
          setActivePage('todo_interviews');
          setViewMode('list');
      } else {
          setUserRole('HR');
          if (notification.title.includes('面谈') || notification.content.includes('面谈')) {
              setActivePage('interviews');
          } else {
              setActivePage('dashboard');
          }
          setViewMode('list');
      }
  };

  const handleInitiateInterviewFromAssessment = (employeeIds: string[]) => {
      setNewInterviewInitialEmployees(employeeIds);
      setNewInterviewDefaultTopic(selectedAssessmentTask || '绩效面谈'); 
      setIsNewInterviewModalOpen(true);
  };

  const handleNewInterviewSubmit = (data: any) => {
      const newSessions: InterviewSession[] = [];
      const employeeIds = data.employeeIds || [];
      const targetIds = employeeIds.length > 0 ? employeeIds : [MOCK_EMPLOYEES[0].id];

      const managerNameMap = {
          'manager': '张伟 (直属上级)',
          'hrbp': 'Lisa (HRBP)',
          'dept_head': '王总 (部门负责人)',
          'skip_manager': '赵总 (隔级上级)'
      }[data.interviewerRole as string] || '张伟';

      const extractedPeriod = data.assessmentTask ? data.assessmentTask.split(' ')[0] + ' ' + data.assessmentTask.split(' ')[1] : '2025 Q4';

      targetIds.forEach((empId: string) => {
          const emp = MOCK_EMPLOYEES.find(e => e.id === empId);
          if (emp) {
              const newSession: InterviewSession = {
                  id: `int_${Date.now()}_${empId}`,
                  employeeId: empId,
                  employeeName: emp.name,
                  managerName: managerNameMap, 
                  date: '', 
                  period: data.topic || '日常面谈', 
                  assessmentCycle: extractedPeriod, 
                  status: Status.NotStarted,
                  type: InterviewType.Regular,
                  templateId: data.templateId,
                  linkedAssessmentId: undefined, 
                  schedulingStatus: 'pending',
                  deadline: data.deadline,
                  method: 'appointment', 
                  requireConfirmation: data.requireConfirmation,
                  signatureType: data.signatureType
              };
              newSessions.push(newSession);
          }
      });

      setSessions(prev => [...newSessions, ...prev]);

      const newNotificationsList: Notification[] = [];
      newNotificationsList.push({
          id: `notif_${Date.now()}_emp`,
          targetRole: 'Employee',
          type: 'task',
          title: '绩效面谈已启动',
          content: `您的【${data.topic}】任务已下发。请关注面谈邀请。`,
          time: '刚刚',
          read: false
      });
      setNotifications(prev => [...newNotificationsList, ...prev]);
      alert(`已创建 ${newSessions.length} 个待排期面谈任务。`);
  };

  const handleOpenScheduleModal = (session: InterviewSession) => {
      setScheduleTargetSession(session);
      setScheduleModalOpen(true);
  };

  const handleConfirmSchedule = (data: any) => {
      if (scheduleTargetSession) {
          const updatedSessionData: InterviewSession = { 
              ...scheduleTargetSession, 
              schedulingStatus: 'scheduled' as const, 
              date: data.date + ' ' + data.time,
              period: data.topic,
              status: Status.NotStarted, 
              method: 'appointment' 
          };
          setSessions(prev => prev.map(s => s.id === scheduleTargetSession.id ? updatedSessionData : s));
          setScheduleModalOpen(false);
          setSelectedSession(updatedSessionData);
          setViewMode('detail');
          if (userRole === 'HR') setActivePage('interviews');
          else setActivePage('todo_interviews'); 
      }
  };

  const handleDirectFeedback = (session: InterviewSession) => {
      const updatedSessions = sessions.map(s => 
          s.id === session.id 
          ? { ...s, status: Status.InProgress, schedulingStatus: 'scheduled' as const, date: new Date().toISOString().split('T')[0], method: 'direct' as const } 
          : s
      );
      setSessions(updatedSessions);
      const updatedSession = updatedSessions.find(s => s.id === session.id);
      if (updatedSession) {
          setSelectedSession(updatedSession);
          setViewMode('detail');
          if (userRole === 'HR') setActivePage('interviews');
          else setActivePage('todo_interviews');
      }
  };

  const handleSubmitFeedback = () => {
      if (selectedSession) {
          setSessions(prev => prev.map(s => 
              s.id === selectedSession.id ? { ...s, status: Status.PendingConfirmation } : s
          ));
          alert('已提交给员工确认！');
          setSelectedSession(null);
          setViewMode('list');
      }
  };

  const handleConfirmCompletion = () => {
      if (selectedSession) {
          setSessions(prev => prev.map(s => 
              s.id === selectedSession.id ? { ...s, status: Status.Completed } : s
          ));
          alert('已完成面谈结果确认！');
          setSelectedSession(null);
          setViewMode('list');
      }
  };

  const handleEmployeeTaskAction = (task: any, actionType?: 'schedule' | 'feedback') => {
      if (activePage === 'todo_interviews' || activePage === 'dashboard') {
          let session = sessions.find(s => s.id === task.id) || sessions.find(s => s.employeeName === task.employee);
          if (!session) session = sessions[0]; 

          if (actionType === 'schedule') {
              handleOpenScheduleModal(session);
          } else if (actionType === 'feedback') {
              handleDirectFeedback(session);
          } else {
               setSelectedSession(session);
               setViewMode('detail');
          }
      } else {
          alert('功能开发中...');
      }
  };

  const handleSelectSession = (session: InterviewSession) => {
      setSelectedSession(session);
      setViewMode('detail');
      setActivePage('interviews'); 
  };

  const handleStartInterview = () => {
      setViewMode('execution');
  };

  const handleEndMeeting = () => {
      if (selectedSession) {
          setSessions(prev => prev.map(s => s.id === selectedSession.id ? { ...s, status: Status.Completed } : s));
          setSelectedSession(prev => prev ? ({ ...prev, status: Status.Completed }) : null);
      }
      setViewMode('detail'); 
  };

  const handleBackToInterviewList = () => {
      setSelectedSession(null);
      setViewMode('list');
  };

  const handleCancelSession = (sessionId: string) => {
      setSessions(prev => prev.filter(s => s.id !== sessionId));
  };

  const handleBatchUpdateSessions = (ids: string[], updates: Partial<InterviewSession>) => {
      setSessions(prev => prev.map(s => ids.includes(s.id) ? { ...s, ...updates } : s));
  };

  const handleSingleCancelSession = (sessionId: string) => {
      if(window.confirm('确定要取消该面谈任务吗？此操作不可恢复。')) {
          setSessions(prev => prev.filter(s => s.id !== sessionId));
      }
  };

  const renderContent = () => {
    if (activePage === 'dashboard') {
        if (userRole === 'Employee') {
            return <EmployeeDashboard 
                sessions={sessions} 
                onEnterMeeting={(s) => { setSelectedSession(s); handleStartInterview(); }} 
                onFeedback={(s) => { handleDirectFeedback(s); }} 
            />;
        }
        return <div className="flex items-center justify-center h-full text-gray-400 bg-white"><div className="text-center"><h2 className="text-xl font-semibold mb-2">欢迎进入 HR 管理后台</h2></div></div>;
    }

    if (activePage === 'my_team_org') return <MyTeamOrgPerformance />;
    if (activePage === 'my_team_sub') return <MyTeamSubordinatePerformance />;
    if (activePage === 'settings') return <SystemSettings onNavigate={setActivePage} />;
    if (activePage === 'template_config') return <TemplateConfigPage onBack={() => setActivePage('settings')} />;
    if (activePage === 'assessments') {
        if (selectedAssessmentTask) {
            return <AssessmentList taskName={selectedAssessmentTask} onInitiateInterview={handleInitiateInterviewFromAssessment} sessions={sessions} onScheduleSession={handleOpenScheduleModal} onSelectSession={handleSelectSession} onCancelSession={handleSingleCancelSession} onBatchUpdateSessions={handleBatchUpdateSessions} onBack={() => setSelectedAssessmentTask(null)} />;
        }
        return <AssessmentTaskList onSelectTask={(taskName) => setSelectedAssessmentTask(taskName)} />;
    }
    if (activePage === 'archives') return <PerformanceArchives />;

    if (activePage === 'interviews' || activePage === 'todo_interviews') {
        if (viewMode === 'execution' && selectedSession) {
            return <InterviewExecution session={selectedSession} onEndMeeting={handleEndMeeting} />;
        }

        if (viewMode === 'detail' && selectedSession) {
          // ROUTING LOGIC: If employee confirms a pending session, show Confirmation View
          if (userRole === 'Employee' && selectedSession.status === Status.PendingConfirmation) {
            return <InterviewConfirmationView session={selectedSession} onBack={handleBackToInterviewList} onConfirm={handleConfirmCompletion} />;
          }
          return <InterviewForm session={selectedSession} onBack={handleBackToInterviewList} onStart={handleStartInterview} onSubmitFeedback={handleSubmitFeedback} onChangeSession={handleSelectSession} />;
        }

        if (activePage === 'interviews' || (userRole === 'Employee' && activePage === 'todo_interviews')) {
            // For Employee "Workbench -> Interview List" flow
            return <InterviewList sessions={sessions} onSelectSession={handleSelectSession} onCreateNew={() => { setNewInterviewInitialEmployees([]); setNewInterviewDefaultTopic('绩效面谈'); setIsNewInterviewModalOpen(true); }} onOpenTemplates={() => setActivePage('template_config')} onScheduleSession={handleOpenScheduleModal} onDirectFeedback={handleDirectFeedback} onCancelSession={handleCancelSession} />;
        }

        const type = activePage === 'todo_interviews' ? 'interviews' : activePage === 'todo_plans' ? 'plans' : 'reviews';
        return <EmployeeTaskTable type={type as any} onAction={handleEmployeeTaskAction} sessions={type === 'interviews' ? sessions : undefined} />;
    }

    if (activePage === 'todo_plans' || activePage === 'todo_reviews') {
        return <EmployeeTaskTable type={activePage === 'todo_plans' ? 'plans' : 'reviews'} onAction={handleEmployeeTaskAction} />;
    }

    return <div className="flex items-center justify-center h-full text-gray-400 bg-white"><div className="text-center"><h2 className="text-xl font-semibold mb-2">欢迎使用智慧绩效 SmartPerf</h2></div></div>;
  };

  const isFullScreenMode = viewMode === 'execution' || viewMode === 'detail';

  return (
    <div className="flex h-screen w-screen overflow-hidden bg-gray-50 text-gray-900 font-sans">
      {/* Sidebar hidden on mobile */}
      {!isFullScreenMode && <div className="hidden md:flex flex-shrink-0 h-full"><Sidebar activePage={activePage} setActivePage={(page) => { setActivePage(page); setViewMode('list'); if (page === 'assessments') setSelectedAssessmentTask(null); }} currentRole={userRole} /></div>}

      <div className="flex-1 flex flex-col min-w-0 pb-16 md:pb-0">
        {!isFullScreenMode && <TopNav currentRole={userRole} onRoleChange={handleRoleChange} notifications={notifications} onMarkRead={handleMarkNotificationRead} onNotificationClick={handleNotificationClick} />}
        <main className="flex-1 overflow-auto relative flex flex-col bg-[#F9FAFB]">{renderContent()}</main>
      </div>

      {/* Mobile Bottom Nav */}
      {userRole === 'Employee' && !isFullScreenMode && (
        <MobileBottomNav activePage={activePage} setActivePage={setActivePage} />
      )}

      <NewInterviewModal isOpen={isNewInterviewModalOpen} onClose={() => setIsNewInterviewModalOpen(false)} onSubmit={handleNewInterviewSubmit} initialEmployeeIds={newInterviewInitialEmployees} defaultTopic={newInterviewDefaultTopic} />
      <ScheduleMeetingModal isOpen={isScheduleModalOpen} onClose={() => setScheduleModalOpen(false)} onConfirm={handleConfirmSchedule} session={scheduleTargetSession} />
    </div>
  );
}

export default App;

import React, { useState, useMemo } from 'react';
import { 
  ChevronLeft, ChevronRight, Settings, Plus, 
  Video, MapPin, Edit3, Clock, Calendar as CalendarIcon,
  CheckCircle2, MessageSquare
} from 'lucide-react';
import { InterviewSession, Status } from '../types';

interface EmployeeDashboardProps {
  sessions: InterviewSession[];
  onEnterMeeting: (session: InterviewSession) => void;
  onFeedback: (session: InterviewSession) => void;
}

const EmployeeDashboard: React.FC<EmployeeDashboardProps> = ({ 
  sessions, 
  onEnterMeeting, 
  onFeedback 
}) => {
  // Demo date: Jan 9, 2026
  const MOCK_TODAY = new Date(2026, 0, 9);

  const [currentDate, setCurrentDate] = useState(MOCK_TODAY);
  const [selectedDate, setSelectedDate] = useState(MOCK_TODAY);

  // Filter for sessions that have a valid date scheduled
  const scheduledSessions = useMemo(() => {
      return sessions.filter(s => s.date && (s.schedulingStatus === 'scheduled' || s.status === Status.Completed || s.status === Status.InProgress));
  }, [sessions]);

  const getDaysInMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (date: Date) => {
    const day = new Date(date.getFullYear(), date.getMonth(), 1).getDay();
    return day === 0 ? 6 : day - 1;
  };

  const handlePrevMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1));
  };

  const handleNextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 1));
  };

  const handleToday = () => {
    setCurrentDate(MOCK_TODAY);
    setSelectedDate(MOCK_TODAY);
  };

  const isSameDay = (d1: Date, d2: Date) => {
    return d1.getDate() === d2.getDate() && 
           d1.getMonth() === d2.getMonth() && 
           d1.getFullYear() === d2.getFullYear();
  };

  const getEventsForDate = (date: Date) => {
      return scheduledSessions.filter(s => {
          const sDate = new Date(s.date.split(' ')[0]); // Handle "YYYY-MM-DD HH:mm"
          return isSameDay(sDate, date);
      });
  };

  const selectedEvents = getEventsForDate(selectedDate);

  const renderCalendarGrid = () => {
    const totalDays = getDaysInMonth(currentDate);
    const startOffset = getFirstDayOfMonth(currentDate);
    const days = [];

    // Empty cells for offset
    for (let i = 0; i < startOffset; i++) {
      days.push(<div key={`empty-${i}`} className="h-16 lg:h-24 w-full"></div>);
    }

    // Days
    for (let i = 1; i <= totalDays; i++) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), i);
      const isSelected = isSameDay(date, selectedDate);
      const isToday = isSameDay(date, MOCK_TODAY);
      const dayEvents = getEventsForDate(date);
      const hasEvents = dayEvents.length > 0;

      const dayOfWeek = date.getDay();

      days.push(
        <div 
          key={i} 
          onClick={() => setSelectedDate(date)}
          className="relative h-16 lg:h-24 border-t border-gray-100 flex flex-col items-center justify-start pt-2 cursor-pointer group hover:bg-gray-50 transition-colors"
        >
          {/* Status Tag (Mock for demo visualization of weekends/workdays) */}
          {(i === 1 || dayOfWeek === 6) && (
             <span className="absolute top-1 left-1 text-[10px] text-green-600 bg-green-50 px-1 rounded transform scale-75 origin-top-left hidden lg:block">休</span>
          )}
          {(i === 4 || dayOfWeek === 1) && (
             <span className="absolute top-1 right-1 text-[10px] text-blue-600 bg-blue-50 px-1 rounded transform scale-75 origin-top-right hidden lg:block">班</span>
          )}

          <div className={`
            w-8 h-8 flex items-center justify-center rounded-full text-sm font-medium mb-1 transition-all
            ${isSelected ? 'bg-primary text-white shadow-md' : 'text-gray-700 group-hover:text-primary'}
            ${isToday && !isSelected ? 'text-primary font-bold' : ''}
          `}>
            {isToday && !isSelected ? '今' : i}
          </div>

          {hasEvents && (
            <div className="flex space-x-1 mt-1">
              <div className={`w-1.5 h-1.5 rounded-full ${isSelected ? 'bg-white' : 'bg-primary'}`}></div>
              {dayEvents.length > 1 && <div className={`w-1.5 h-1.5 rounded-full ${isSelected ? 'bg-white' : 'bg-primary'}`}></div>}
            </div>
          )}
        </div>
      );
    }

    return days;
  };

  const getEventTime = (dateStr: string) => {
      if (dateStr.includes(' ')) {
          return dateStr.split(' ')[1];
      }
      return '全天'; // Or default time
  };

  return (
    <div className="p-4 lg:p-6 h-full flex flex-col bg-white overflow-y-auto lg:overflow-hidden">
        <div className="flex flex-col lg:flex-row gap-8 h-full">

            {/* Left: Calendar */}
            <div className="flex-1 flex flex-col h-auto lg:h-full">
                <div className="flex items-center justify-between mb-6 shrink-0">
                    <div className="flex items-center space-x-4">
                        <h2 className="text-xl font-bold text-gray-800">日历</h2>
                        <div className="flex items-center space-x-2 text-sm text-gray-600">
                            <button onClick={handlePrevMonth} className="p-1 hover:bg-gray-100 rounded text-gray-400 hover:text-gray-600"><ChevronLeft size={18}/></button>
                            <span className="font-medium font-mono text-base">{currentDate.getFullYear()}年{String(currentDate.getMonth() + 1).padStart(2, '0')}月</span>
                            <button onClick={handleNextMonth} className="p-1 hover:bg-gray-100 rounded text-gray-400 hover:text-gray-600"><ChevronRight size={18}/></button>
                            <button onClick={handleToday} className="text-primary hover:text-primary-700 ml-2">今天</button>
                        </div>
                    </div>
                    <button className="text-gray-400 hover:text-gray-600 p-2 hover:bg-gray-50 rounded-full">
                        <Settings size={18} />
                    </button>
                </div>

                <div className="flex-1 flex flex-col">
                    {/* Week Header */}
                    <div className="grid grid-cols-7 mb-2">
                        {['一', '二', '三', '四', '五', '六', '日'].map(day => (
                            <div key={day} className="text-center text-xs font-medium text-gray-400 py-2">
                                {day}
                            </div>
                        ))}
                    </div>
                    {/* Days Grid */}
                    <div className="grid grid-cols-7 flex-1 content-start border-b border-gray-100 lg:border-none lg:overflow-y-auto lg:custom-scrollbar">
                        {renderCalendarGrid()}
                    </div>

                    <div className="flex justify-end mt-2 text-primary hover:text-primary-700 cursor-pointer text-xs flex items-center mb-4 lg:mb-0">
                        详细日历 <ChevronRight size={12} className="ml-0.5" />
                    </div>
                </div>
            </div>

            {/* Right: Daily Agenda */}
            <div className="w-full lg:w-[400px] bg-white border-t lg:border-t-0 lg:border-l border-gray-100 pt-6 lg:pt-0 lg:pl-8 flex flex-col lg:h-full">
                <div className="flex items-center justify-between mb-6 shrink-0">
                    <h3 className="text-lg font-bold text-gray-800">当日事项 ({selectedEvents.length})</h3>
                    <button className="p-1.5 hover:bg-gray-100 rounded text-gray-400 hover:text-gray-600">
                        <Plus size={20} />
                    </button>
                </div>

                <div className="flex-1 lg:overflow-y-auto lg:custom-scrollbar pr-2 space-y-4">
                    {selectedEvents.length > 0 ? (
                        selectedEvents.map(session => {
                            const isCompleted = session.status === Status.Completed;
                            const isOnline = session.method === 'appointment';
                            const timeStr = getEventTime(session.date);

                            return (
                                <div key={session.id} className="bg-primary-50/30 rounded-xl p-4 border border-primary-100 hover:shadow-sm transition-all group">
                                    <div className="flex items-start mb-3">
                                        <div className={`mt-0.5 mr-3 p-1.5 rounded-lg ${isOnline ? 'bg-blue-100 text-blue-600' : 'bg-orange-100 text-orange-600'}`}>
                                            {isOnline ? <Video size={16} /> : <MapPin size={16} />}
                                        </div>
                                        <div className="flex-1">
                                            <h4 className="text-sm font-bold text-gray-800 leading-snug mb-1">
                                                面谈：{session.period}
                                            </h4>
                                            <div className="text-xs text-gray-500 mb-1">
                                                对象：{session.employeeName}
                                            </div>
                                            <div className="text-xs text-gray-500 font-mono flex items-center">
                                                <Clock size={10} className="mr-1" />
                                                {timeStr}
                                            </div>
                                        </div>
                                    </div>

                                    <div className="flex justify-end space-x-3 pt-2 border-t border-primary-100/50">
                                        {isCompleted ? (
                                            <span className="text-xs font-medium text-green-600 flex items-center">
                                                <CheckCircle2 size={12} className="mr-1" /> 已完成
                                            </span>
                                        ) : (
                                            <>
                                                {isOnline && (
                                                    <button 
                                                        onClick={() => onEnterMeeting(session)}
                                                        className="text-xs font-medium text-primary hover:text-primary-700 transition-colors flex items-center"
                                                    >
                                                        进入会议
                                                    </button>
                                                )}
                                                <button 
                                                    onClick={() => onFeedback(session)}
                                                    className="text-xs font-medium text-gray-500 hover:text-gray-700 transition-colors flex items-center"
                                                >
                                                    <Edit3 size={12} className="mr-1" /> 填写反馈
                                                </button>
                                            </>
                                        )}
                                    </div>
                                </div>
                            );
                        })
                    ) : (
                        <div className="flex flex-col items-center justify-center h-32 lg:h-64 text-gray-400">
                            <div className="w-16 h-16 bg-gray-50 rounded-full flex items-center justify-center mb-4">
                                <CalendarIcon size={24} className="opacity-50" />
                            </div>
                            <p className="text-sm">今日无面谈安排</p>
                        </div>
                    )}

                    {/* Placeholder for later times */}
                    {selectedEvents.length > 0 && (
                        <div className="opacity-40">
                             <div className="flex items-center text-xs text-gray-300 mb-2 mt-6">
                                 <span className="w-2 h-2 rounded-full bg-gray-200 mr-2"></span>
                                 晚些时候
                             </div>
                             <div className="bg-gray-50 rounded-xl p-4 border border-gray-100">
                                 <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                                 <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                             </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    </div>
  );
};

export default EmployeeDashboard;

import React, { useState, useEffect } from 'react';
import { 
  Plus, Search, Clock, 
  Edit3, AlertCircle, ArrowRight, FileText, History, X, Bell,
  Star, Paperclip, Table, Grid,
  Briefcase, CheckCircle, Calendar as CalendarIcon, PlayCircle, Trash2, MoreHorizontal, AlertTriangle, User
} from 'lucide-react';
import { InterviewSession, Status, InterviewType, HistoricalRecord } from '../types';
import { MOCK_HISTORY_RECORDS, MOCK_TEMPLATES, MOCK_EMPLOYEES, MOCK_PERFORMANCE_RECORDS } from '../constants';

interface InterviewListProps {
  sessions: InterviewSession[];
  onSelectSession: (session: InterviewSession) => void;
  onCreateNew: () => void;
  onOpenTemplates: () => void;
  onScheduleSession: (session: InterviewSession) => void;
  onDirectFeedback: (session: InterviewSession) => void;
  onCancelSession: (sessionId: string) => void;
}

const InterviewList: React.FC<InterviewListProps> = ({ 
  sessions, 
  onSelectSession, 
  onCreateNew, 
  onOpenTemplates, 
  onScheduleSession, 
  onDirectFeedback,
  onCancelSession
}) => {
  type TabType = 'all' | 'schedule' | 'start' | 'feedback' | 'done';
  const [activeTab, setActiveTab] = useState<TabType>('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedIds, setSelectedIds] = useState<Set<string>>(new Set());

  // Interaction State
  const [showHistoryFor, setShowHistoryFor] = useState<string | null>(null); // employeeId
  const [showTemplateFor, setShowTemplateFor] = useState<string | null>(null); // templateId
  const [openActionId, setOpenActionId] = useState<string | null>(null);

  // Cancellation Modal State
  const [cancelModalOpen, setCancelModalOpen] = useState(false);
  const [sessionToCancel, setSessionToCancel] = useState<InterviewSession | null>(null);

  // Close dropdown when clicking outside
  useEffect(() => {
      const handleClickOutside = () => setOpenActionId(null);
      window.addEventListener('click', handleClickOutside);
      return () => window.removeEventListener('click', handleClickOutside);
  }, []);

  // --- Statistics Calculation ---
  const stats = {
      total: sessions.length,
      completed: sessions.filter(s => s.status === Status.Completed).length,
      inProgress: sessions.filter(s => s.status === Status.InProgress || s.status === Status.PendingConfirmation).length,
      notStarted: sessions.filter(s => s.status === Status.NotStarted).length,
  };

  // Filter Logic
  const filteredSessions = sessions.filter(session => {
    // Tab Filter
    let matchesTab = false;
    switch (activeTab) {
        case 'all':
            matchesTab = true;
            break;
        case 'schedule':
            // 待排期: 未开始 且 (排期状态为 pending 或 未定义)
            matchesTab = session.status === Status.NotStarted && (session.schedulingStatus === 'pending' || !session.schedulingStatus);
            break;
        case 'start':
            // 待开始: 未开始 且 排期状态为 scheduled
            matchesTab = session.status === Status.NotStarted && session.schedulingStatus === 'scheduled';
            break;
        case 'feedback':
            // 待反馈: 进行中 或 待确认 (Manager perspective)
            matchesTab = session.status === Status.InProgress || session.status === Status.PendingConfirmation;
            break;
        case 'done':
            // 已完成: 已完成
            matchesTab = session.status === Status.Completed;
            break;
    }

    // Search Filter
    const matchesSearch = session.employeeName.toLowerCase().includes(searchTerm.toLowerCase()) || 
                          session.managerName.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesTab && matchesSearch;
  });

  // Calculate counts for tabs
  const getCount = (tab: TabType) => {
      if (tab === 'all') return sessions.length;
      return sessions.filter(session => {
        if (tab === 'schedule') return session.status === Status.NotStarted && (session.schedulingStatus === 'pending' || !session.schedulingStatus);
        if (tab === 'start') return session.status === Status.NotStarted && session.schedulingStatus === 'scheduled';
        if (tab === 'feedback') return session.status === Status.InProgress || session.status === Status.PendingConfirmation;
        if (tab === 'done') return session.status === Status.Completed;
        return false;
      }).length;
  };

  // Selection Logic
  const handleSelectAll = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.checked) {
      setSelectedIds(new Set(filteredSessions.map(s => s.id)));
    } else {
      setSelectedIds(new Set());
    }
  };

  const handleSelectOne = (id: string) => {
    const newSet = new Set(selectedIds);
    if (newSet.has(id)) newSet.delete(id);
    else newSet.add(id);
    setSelectedIds(newSet);
  };

  const handleHistoryClick = (record: HistoricalRecord, employeeId: string) => {
      const employee = MOCK_EMPLOYEES.find(e => e.id === employeeId);
      const session: InterviewSession = {
          id: record.id,
          employeeId: employeeId,
          employeeName: employee?.name || 'Unknown',
          managerName: record.manager,
          date: record.date,
          period: record.type,
          status: Status.Completed,
          type: InterviewType.Regular,
          method: 'appointment',
          templateId: 't1', // Default template for history records
          schedulingStatus: 'scheduled'
      };
      onSelectSession(session);
  };

  const handleRemind = (e: React.MouseEvent, session: InterviewSession) => {
      e.stopPropagation();
      setOpenActionId(null);
      if (window.confirm(`确定要向 ${session.employeeName} 发送催办提醒吗？`)) {
          alert(`已成功发送催办提醒给 ${session.employeeName}`);
      }
  };

  const handleBatchRemind = () => {
      if (selectedIds.size === 0) return;
      if (window.confirm(`确定要对选中的 ${selectedIds.size} 个任务进行批量催办吗？`)) {
          alert(`已成功发送 ${selectedIds.size} 条催办提醒`);
          setSelectedIds(new Set());
      }
  };

  const handleCancelClick = (e: React.MouseEvent, session: InterviewSession) => {
      e.stopPropagation();
      setOpenActionId(null);
      setSessionToCancel(session);
      setCancelModalOpen(true);
  };

  const confirmCancel = () => {
      if (sessionToCancel) {
          onCancelSession(sessionToCancel.id);
          setCancelModalOpen(false);
          setSessionToCancel(null);
      }
  };

  // Helper to determine display context for a session
  const getSessionContext = (session: InterviewSession): 'schedule' | 'start' | 'feedback' | 'done' => {
      if (session.status === Status.Completed) return 'done';
      if (session.status === Status.InProgress || session.status === Status.PendingConfirmation) return 'feedback';
      if (session.status === Status.NotStarted && session.schedulingStatus === 'scheduled') return 'start';
      return 'schedule';
  };

  // Helper for rendering columns
  const renderTime = (session: InterviewSession) => {
      const context = activeTab === 'all' ? getSessionContext(session) : activeTab;

      if (context === 'done') {
          return <div className="text-gray-500 font-mono text-sm">{session.date?.split(' ')[0] || '-'}</div>;
      }

      // Show scheduled time for "To Start" or "Feedback"
      if (session.date && (context === 'start' || context === 'feedback')) {
          return (
            <div className="flex flex-col">
                <span className="text-gray-800 font-medium font-mono text-sm">{session.date.split(' ')[0]}</span>
                <span className="text-gray-400 text-xs mt-0.5 font-mono">
                    {session.date.split(' ')[1] || '10:00'} - {session.date.split(' ')[1] ? '11:00' : '10:30'}
                </span>
            </div>
          );
      }

      // Show Deadline for "To Schedule"
      if (session.deadline && context === 'schedule') {
          const deadlineDate = new Date(session.deadline);
          const today = new Date();
          const diffTime = deadlineDate.getTime() - today.getTime();
          const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

          const isUrgent = diffDays <= 3 && diffDays >= 0;
          const isOverdue = diffDays < 0;

          return (
              <div className="flex flex-col justify-center">
                  <div className="flex items-center text-gray-500 text-xs mb-1">
                      <span className="mr-1">截止:</span>
                      <span className="font-mono">{session.deadline}</span>
                  </div>

                  {isOverdue ? (
                      <span className="inline-flex items-center text-red-600 text-[10px] font-medium bg-red-50 px-1.5 py-0.5 rounded w-fit">
                          <AlertCircle size={10} className="mr-1" /> 已逾期 {Math.abs(diffDays)} 天
                      </span>
                  ) : isUrgent ? (
                      <span className="inline-flex items-center text-orange-600 text-[10px] font-medium bg-orange-50 px-1.5 py-0.5 rounded w-fit">
                          <Clock size={10} className="mr-1" /> 仅剩 {diffDays} 天
                      </span>
                  ) : (
                      <span className="text-gray-400 text-[10px]">待预约时间</span>
                  )}
              </div>
          );
      }
      return <span className="text-gray-400 text-xs">-</span>;
  };

  const renderStatusBadge = (session: InterviewSession) => {
      let label = session.status as string;
      let styleClass = '';
      let handler = '';

      // Determine Status Label & Color
      if (session.status === Status.NotStarted) {
          if (session.schedulingStatus === 'pending' || !session.schedulingStatus) {
              label = '待安排';
              styleClass = 'bg-gray-100 text-gray-600 border-gray-200';
              handler = session.managerName.split(' ')[0]; // Manager needs to schedule
          } else {
              label = '未开始';
              styleClass = 'bg-gray-100 text-gray-600 border-gray-200';
              handler = session.managerName.split(' ')[0]; // Manager needs to start
          }
      } else if (session.status === Status.InProgress) {
          label = '进行中';
          styleClass = 'bg-blue-50 text-blue-600 border-blue-100';
          handler = session.managerName.split(' ')[0]; // Manager needs to submit
      } else if (session.status === Status.PendingConfirmation) {
          label = '待确认';
          styleClass = 'bg-orange-50 text-orange-600 border-orange-100';
          handler = session.employeeName; // Employee needs to confirm
      } else if (session.status === Status.Completed) {
          label = '已完成';
          styleClass = 'bg-green-50 text-green-600 border-green-100';
          handler = ''; // No pending handler
      } else {
          // Default/Archived
          label = session.status;
          styleClass = 'bg-gray-50 text-gray-400 border-gray-200';
      }

      return (
          <div className="flex flex-col">
              <span className={`inline-flex items-center px-2 py-0.5 rounded text-xs font-medium border w-fit ${styleClass}`}>
                  {label}
              </span>
              {handler && (
                  <span className="text-[10px] text-gray-400 mt-1 ml-0.5 hidden md:block">
                      待处理: {handler}
                  </span>
              )}
          </div>
      );
  };

  const renderAssessmentResult = (session: InterviewSession) => {
      // Find performance record based on employeeId and assessmentCycle
      const record = MOCK_PERFORMANCE_RECORDS.find(r => r.employeeId === session.employeeId && r.period === session.assessmentCycle);

      if (!record) {
          return <span className="text-gray-400">-</span>;
      }

      // Determine style based on grade
      let gradeClass = 'bg-gray-100 text-gray-600';
      if (['S', 'A', 'A+'].includes(record.grade)) {
          gradeClass = 'bg-green-100 text-green-700 border-green-200';
      } else if (['B+', 'B'].includes(record.grade)) {
          gradeClass = 'bg-blue-100 text-blue-700 border-blue-200';
      } else if (['C', 'D'].includes(record.grade)) {
          gradeClass = 'bg-orange-100 text-orange-700 border-orange-200';
      }

      return (
          <div className="flex items-center space-x-2">
              <span className="font-bold text-gray-800 text-sm">{record.overallScore}</span>
              <span className={`text-[10px] font-bold px-1.5 py-0.5 rounded border ${gradeClass}`}>
                  {record.grade}
              </span>
          </div>
      );
  };

  const renderAction = (session: InterviewSession) => {
      const isPendingSchedule = session.status === Status.NotStarted && (session.schedulingStatus === 'pending' || !session.schedulingStatus);
      const isScheduled = session.status === Status.NotStarted && session.schedulingStatus === 'scheduled';
      const isInProgress = session.status === Status.InProgress;
      const isPendingConfirmation = session.status === Status.PendingConfirmation;
      const isCompleted = session.status === Status.Completed;

      interface ActionItem {
          label: string;
          onClick: (e: React.MouseEvent) => void;
          variant?: 'primary' | 'danger' | 'default';
      }

      let actions: ActionItem[] = [];

      if (isPendingSchedule) {
          actions = [
              { label: '预约会议', onClick: () => onScheduleSession(session), variant: 'primary' },
              { label: '直接反馈', onClick: () => onDirectFeedback(session), variant: 'default' },
              { label: '催办', onClick: (e) => handleRemind(e, session), variant: 'default' },
              { label: '取消', onClick: (e) => handleCancelClick(e, session), variant: 'danger' },
          ];
      } else if (isScheduled) {
          actions = [
              { label: '重新安排', onClick: () => onScheduleSession(session), variant: 'primary' },
              { label: '直接反馈', onClick: () => onDirectFeedback(session), variant: 'default' },
              { label: '取消', onClick: (e) => handleCancelClick(e, session), variant: 'danger' },
          ];
      } else if (isInProgress) {
          actions = [
              { label: '查看详情', onClick: () => onSelectSession(session), variant: 'primary' },
          ];
      } else if (isPendingConfirmation) {
          actions = [
              { label: '详情', onClick: () => onSelectSession(session), variant: 'default' },
              { label: '直接确认', onClick: () => alert('已代确认'), variant: 'primary' },
              { label: '催办', onClick: (e) => handleRemind(e, session), variant: 'default' },
          ];
      } else if (isCompleted) {
          actions = [
              { label: '查看详情', onClick: () => onSelectSession(session), variant: 'primary' },
              { label: '再次发起', onClick: () => onCreateNew(), variant: 'default' },
          ];
      }

      const visibleActions = actions.slice(0, 2);
      const hiddenActions = actions.slice(2);

      return (
          <div className="flex items-center justify-end space-x-3 text-xs">
              {visibleActions.map((action, idx) => (
                  <button
                      key={idx}
                      onClick={action.onClick}
                      className={`font-medium whitespace-nowrap transition-colors ${
                          action.variant === 'primary' ? 'text-primary hover:text-primary-700' :
                          action.variant === 'danger' ? 'text-gray-500 hover:text-red-600' :
                          'text-gray-500 hover:text-gray-800'
                      }`}
                  >
                      {action.label}
                  </button>
              ))}

              {(visibleActions.length > 0 && hiddenActions.length > 0) && (
                  <div className="w-px h-3 bg-gray-300"></div>
              )}

              {hiddenActions.length > 0 && (
                  <div className="relative">
                      <button 
                          onClick={(e) => {
                              e.stopPropagation();
                              setOpenActionId(openActionId === session.id ? null : session.id);
                          }}
                          className={`p-1 rounded-md hover:bg-gray-100 text-gray-400 hover:text-gray-600 transition-colors ${openActionId === session.id ? 'bg-gray-100 text-gray-600' : ''}`}
                      >
                          <MoreHorizontal size={16} />
                      </button>

                      {openActionId === session.id && (
                          <>
                              <div className="fixed inset-0 z-40" onClick={() => setOpenActionId(null)}></div>
                              <div className="absolute right-0 top-full mt-1 w-24 bg-white rounded-lg shadow-xl border border-gray-100 z-50 py-1 text-xs text-gray-700 animate-in fade-in zoom-in-95 duration-100 origin-top-right">
                                  {hiddenActions.map((action, idx) => (
                                      <button 
                                          key={idx}
                                          onClick={(e) => {
                                              action.onClick(e);
                                              setOpenActionId(null);
                                          }}
                                          className={`w-full text-left px-4 py-2 hover:bg-gray-50 block transition-colors ${
                                              action.variant === 'danger' ? 'text-red-600 hover:bg-red-50' : 'text-gray-700'
                                          }`}
                                      >
                                          {action.label}
                                      </button>
                                  ))}
                              </div>
                          </>
                      )}
                  </div>
              )}
          </div>
      );
  };

  // Mobile Card Renderer
  const renderMobileCard = (session: InterviewSession) => {
      const templateName = MOCK_TEMPLATES.find(t => t.id === session.templateId)?.name || '标准模板';

      return (
          <div key={session.id} className="bg-white border border-gray-200 rounded-xl p-4 shadow-sm mb-3 relative" onClick={() => onSelectSession(session)}>
              <div className="flex justify-between items-start mb-2">
                  <div className="flex items-center">
                      <div className="w-8 h-8 rounded-full bg-gray-100 flex items-center justify-center mr-3 text-xs font-bold text-gray-600">
                          {session.employeeName.charAt(0)}
                      </div>
                      <div>
                          <div className="text-sm font-bold text-gray-900">{session.employeeName}</div>
                          <div className="text-xs text-gray-500">{session.period}</div>
                      </div>
                  </div>
                  {renderStatusBadge(session)}
              </div>

              <div className="grid grid-cols-2 gap-2 text-xs text-gray-600 mb-3 mt-3 bg-gray-50 p-2 rounded-lg">
                  <div>
                      <span className="text-gray-400 block mb-0.5">面谈官</span>
                      {session.managerName.split(' ')[0]}
                  </div>
                  <div>
                      <span className="text-gray-400 block mb-0.5">时间</span>
                      {session.date || session.deadline || '待定'}
                  </div>
                  <div>
                      <span className="text-gray-400 block mb-0.5">考核结果</span>
                      {renderAssessmentResult(session)}
                  </div>
              </div>

              <div className="flex justify-end pt-2 border-t border-gray-100">
                  {renderAction(session)}
              </div>
          </div>
      );
  };

  return (
    <div className="p-4 lg:p-6 h-full flex flex-col bg-white relative">

      {/* 1. Statistics Panel - Hidden on Mobile to save space */}
      <div className="hidden lg:block bg-white border border-gray-200 rounded-xl p-5 mb-6 shadow-sm">
          {/* Stats Cards Grid */}
          <div className="grid grid-cols-4 gap-4">
              <div className="bg-gray-50/50 border border-gray-100 rounded-xl p-3.5 flex items-center">
                  <div className="w-10 h-10 rounded-full bg-blue-50 text-blue-600 flex items-center justify-center mr-3 shrink-0">
                      <Briefcase size={18} />
                  </div>
                  <div>
                      <div className="text-gray-500 text-xs font-medium mb-0.5">任务总数</div>
                      <div className="text-xl font-bold text-gray-900">{stats.total}</div>
                  </div>
              </div>
              {/* ... other stats ... */}
              <div className="bg-green-50/30 border border-green-100 rounded-xl p-3.5 flex items-center">
                  <div className="w-10 h-10 rounded-full bg-green-100 text-green-600 flex items-center justify-center mr-3 shrink-0">
                      <CheckCircle size={18} />
                  </div>
                  <div>
                      <div className="text-gray-500 text-xs font-medium mb-0.5">已完成</div>
                      <div className="text-xl font-bold text-green-700">{stats.completed}</div>
                  </div>
              </div>
              <div className="bg-blue-50/30 border border-blue-100 rounded-xl p-3.5 flex items-center">
                  <div className="w-10 h-10 rounded-full bg-blue-100 text-blue-600 flex items-center justify-center mr-3 shrink-0">
                      <PlayCircle size={18} />
                  </div>
                  <div>
                      <div className="text-gray-500 text-xs font-medium mb-0.5">进行中</div>
                      <div className="text-xl font-bold text-blue-700">{stats.inProgress}</div>
                  </div>
              </div>
              <div className="bg-gray-50/50 border border-gray-200 rounded-xl p-3.5 flex items-center">
                  <div className="w-10 h-10 rounded-full bg-gray-100 text-gray-500 flex items-center justify-center mr-3 shrink-0">
                      <CalendarIcon size={18} />
                  </div>
                  <div>
                      <div className="text-gray-500 text-xs font-medium mb-0.5">待开始</div>
                      <div className="text-xl font-bold text-gray-700">{stats.notStarted}</div>
                  </div>
              </div>
          </div>
      </div>

      {/* Top Header Section */}
      <div className="flex flex-col mb-4">
        {/* Tabs - Scrollable on Mobile */}
        <div className="flex items-center space-x-1 border-b border-gray-300 mb-4 lg:mb-6 overflow-x-auto hide-scrollbar">
            <TabItem active={activeTab === 'all'} onClick={() => setActiveTab('all')} label="全部" count={getCount('all')} />
            <TabItem active={activeTab === 'schedule'} onClick={() => setActiveTab('schedule')} label="待排期" count={getCount('schedule')} />
            <TabItem active={activeTab === 'start'} onClick={() => setActiveTab('start')} label="待开始" count={getCount('start')} />
            <TabItem active={activeTab === 'feedback'} onClick={() => setActiveTab('feedback')} label="待反馈" count={getCount('feedback')} />
            <TabItem active={activeTab === 'done'} onClick={() => setActiveTab('done')} label="已完成" count={getCount('done')} />
        </div>

        {/* Action Bar */}
        <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center bg-gray-50/50 p-2 rounded-lg border border-gray-200/50 gap-2">
            {/* Left: Search */}
            <div className="relative group w-full lg:w-auto lg:ml-1">
                <input 
                    type="text" 
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="搜索姓名、面谈官..." 
                    className="pl-8 pr-4 py-1.5 bg-white border border-gray-300 rounded text-sm focus:outline-none focus:ring-1 focus:ring-primary w-full lg:w-64 transition-all"
                />
                <Search className="absolute left-2.5 top-2.5 text-gray-400 group-hover:text-gray-500" size={14} />
            </div>

            {/* Right: Buttons */}
            <div className="flex items-center space-x-2 w-full lg:w-auto lg:mr-1 overflow-x-auto">
                {selectedIds.size > 0 && (
                    <button 
                        onClick={handleBatchRemind}
