            className={`bg-white md:rounded-2xl border border-gray-200 shadow-sm overflow-hidden flex flex-col shrink-0 relative
                ${mobileTab === 'form' ? 'hidden md:flex' : 'flex w-full md:w-auto'}
            `}
            style={{ width: window.innerWidth >= 768 ? `${leftPanelWidth}%` : '100%', minWidth: '320px' }}
        >
             {renderLeftContent()}
        </div>

        {/* --- RESIZER (Desktop Only) --- */}
        <div 
            className="w-4 hover:w-4 cursor-col-resize z-10 hidden md:flex items-center justify-center group relative -ml-2 -mr-2 select-none"
            onMouseDown={startResizing}
        >
             <div className="w-1 h-8 bg-gray-200 rounded-full group-hover:bg-blue-400 transition-colors"></div>
        </div>

        {/* --- RIGHT PANEL --- */}
        {/* Hidden on mobile if tab is 'analysis' */}
        <div 
            className={`flex-col min-w-0 bg-white md:rounded-2xl border border-gray-200 shadow-sm overflow-hidden
                ${mobileTab === 'analysis' ? 'hidden md:flex' : 'flex flex-1'}
            `}
        >
            {/* Tabs Header (If applicable) - Desktop Only */}
            {!isDirect && !isPendingConfirmation && (
                <div className="bg-white border-b border-gray-100 px-6 pt-2 hidden md:block">
                     <div className="flex space-x-6 overflow-x-auto hide-scrollbar">
                         {/* ... Desktop Tabs ... */}
                         <button onClick={() => setActiveTab('outline')} className="pb-3 text-sm font-medium text-blue-600 border-b-2 border-blue-600">面谈大纲</button>
                     </div>
                </div>
            )}

            <div className="flex-1 overflow-hidden relative">
                {renderRightContent()}
            </div>
        </div>
      </div>

      {/* ... Modals (Drawer, Share, Conflict) ... */}
      {/* ... keeping existing modal logic ... */}
      {isDrawerOpen && (
          <div className="absolute inset-0 z-30 flex justify-end bg-black/20 backdrop-blur-[1px] transition-all">
              <div className="w-full md:w-[800px] bg-white h-full shadow-2xl flex flex-col animate-in slide-in-from-right duration-300">
                  <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-gray-50">
                      <h3 className="font-bold text-gray-800 flex items-center">
                          <FileText size={18} className="mr-2 text-blue-600" />
                          绩效考核表详情
                      </h3>
                      <button onClick={() => setIsDrawerOpen(false)} className="p-1 rounded-full hover:bg-gray-200 text-gray-500">
                          <X size={20} />
                      </button>
                  </div>
                  <div className="flex-1 overflow-hidden p-0">
                      <AssessmentDetailTable detail={assessmentDetail} period={session.period} />
                  </div>
              </div>
          </div>
      )}

      {isShareModalOpen && (
          <div className="fixed inset-0 z-[60] flex items-center justify-center bg-black/60 backdrop-blur-sm p-4">
              <div className="bg-white rounded-2xl shadow-2xl w-full max-w-lg overflow-hidden">
                  <div className="px-6 py-5 border-b border-gray-100 flex justify-between items-center bg-white">
                      <h3 className="text-xl font-bold text-gray-900">确认提交</h3>
                      <button onClick={() => setIsShareModalOpen(false)}><X size={20} /></button>
                  </div>
                  <div className="p-6">
                      <p className="text-sm text-gray-600 mb-4">即将提交给员工确认。请确认以下内容可见性：</p>
                      {/* ... Share Config UI ... */}
                      <div className="space-y-2">
                          <div className="flex items-center justify-between p-3 border rounded-lg bg-blue-50 border-blue-200">
                              <span className="text-sm font-bold text-gray-800">绩效面谈记录表</span>
                              <span className="text-xs bg-blue-100 text-blue-700 px-2 py-1 rounded">必选</span>
                          </div>
                          {!isDirect && (
                              <div className="flex items-center justify-between p-3 border rounded-lg">
                                  <span className="text-sm text-gray-600">智能纪要</span>
                                  <input type="checkbox" checked={shareConfig.items.summary} onChange={() => toggleShareItem('summary')} />
                              </div>
                          )}
                      </div>
                  </div>
                  <div className="px-6 py-5 bg-white border-t border-gray-100 flex justify-end space-x-3">
                      <button onClick={() => setIsShareModalOpen(false)} className="px-5 py-2.5 bg-white border border-gray-300 rounded-xl text-sm">取消</button>
                      <button onClick={handleConfirmShare} className="px-8 py-2.5 bg-gray-900 text-white rounded-xl text-sm">确认</button>
                  </div>
              </div>
          </div>
      )}

    </div>
  );
};

export default InterviewForm;
